interface IHegicStrategy {
    event Acquired(
        uint256 indexed id,
        uint256 amount,
        uint256 premium,
        uint256 strike,
        uint32 expiration
    );

    event AcquiredStrangle(
        uint256 indexed id,
        uint256 amount,
        uint256 premium,
        uint256 callStrike,
        uint256 putStrike,
        uint32 expiration
    );

    event AcquiredSpread(
        uint256 indexed id,
        uint256 amount,
        uint256 premium,
        uint256 atmStrike,
        uint256 otmStrike,
        uint32 expiration
    );

    event SetLimit(uint256 limit);

    function getLockedByStrategy() external view returns (uint256 amount);

    function lockedLimit() external view returns (uint256 lockedLimit);
}
