import "../Pool/IHegicOperationalTreasury.sol";

/**
 * @author 0mllwntrmt3
 * @title Hegic Protocol hardcore-beta Unlocker Contract
 * @notice The contract that allows to automatically unlocks an array of options
 **/
contract Unlocker {
    /**
     * @notice Unlocks the array of options.
     * @param optionIDs The array of options
     **/
    function unlockAll(
        IHegicOperationalTreasury pool,
        uint256[] calldata optionIDs
    ) external {
